import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import tokenData from "./nawah_tokens.json";

function App() {
  const [account, setAccount] = useState(null);
  const [balance, setBalance] = useState("0");
  const [transferTo, setTransferTo] = useState("");
  const [transferAmount, setTransferAmount] = useState("");
  const [status, setStatus] = useState("");

  const connectWallet = async () => {
    if(window.ethereum){
      try {
        const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
        setAccount(accounts[0]);
      } catch(e){ console.error(e); alert("فشل الاتصال بالمحفظة"); }
    } else {
      alert("MetaMask غير مثبت!");
    }
  };

  const fetchBalance = async () => {
    if(!account) return;
    const provider = new ethers.BrowserProvider(window.ethereum);
    try{
      const token = tokenData[0]; // USNT فقط
      const contract = new ethers.Contract(
        token.address,
        ["function balanceOf(address owner) view returns (uint256)", "function decimals() view returns (uint8)"],
        provider
      );
      const raw = await contract.balanceOf(account);
      const decimals = await contract.decimals();
      setBalance(ethers.formatUnits(raw, decimals));
    } catch(e){ console.error(e); setBalance("Error"); }
  };

  useEffect(() => { if(account) fetchBalance(); }, [account]);

  const handleTransfer = async (e) => {
    e.preventDefault();
    setStatus("Processing...");
    if(!account){ setStatus("Wallet not connected!"); return; }
    const token = tokenData[0];
    const provider = new ethers.BrowserProvider(window.ethereum);
    const signer = await provider.getSigner();
    try{
      const contract = new ethers.Contract(
        token.address,
        ["function transfer(address to, uint256 amount) returns (bool)", "function decimals() view returns (uint8)"],
        signer
      );
      const decimals = await contract.decimals();
      const amountParsed = ethers.parseUnits(transferAmount, decimals);
      const tx = await contract.transfer(transferTo, amountParsed);
      setStatus(`Transaction sent: ${tx.hash}`);
      await tx.wait();
      setStatus(`Transaction confirmed: ${tx.hash}`);
      fetchBalance();
    } catch(e){ console.error(e); setStatus("Error: "+e.message); }
  };

  return (
    <div style={{ padding:"20px", fontFamily:"Arial, sans-serif" }}>
      <h1>USNT Dashboard</h1>
      {!account ? (
        <button onClick={connectWallet} style={{ padding:"10px 20px", fontSize:"16px" }}>
          Connect MetaMask
        </button>
      ) : (
        <>
        <p>Connected Account: {account}</p>
        <p>USNT Balance: {balance}</p>
        <h2>Transfer USNT</h2>
        <form onSubmit={handleTransfer}>
          <label>To Address:<input type="text" value={transferTo} onChange={e=>setTransferTo(e.target.value)} required/></label><br/>
          <label>Amount:<input type="number" step="any" value={transferAmount} onChange={e=>setTransferAmount(e.target.value)} required/></label><br/>
          <button type="submit" style={{ padding:"8px 16px", marginTop:"10px" }}>Send</button>
        </form>
        <p>Status: {status}</p>
        </>
      )}
    </div>
  );
}

export default App;
